import React, {useState, useEffect} from 'react';
import Editor from './components/Editor';
import Output from './components/Output';
import Gallery from './components/Gallery';
import BorderRadius from './components/BorderRadius';

function App() {

  const [color, setColor] = useState("");
  const [width, setWidth] = useState("");
  const [height, setHeight] = useState("");

  useEffect(() => {
    const handleSetWidth = (w) => {
      setWidth(w);
    };
    handleSetWidth(w)

  })

  const handleSetColor = (c) => {
    setColor(prevState => {
      return ([...prevState, c])
    });
  }

  // const handleSetWidth = (w) => {
  //   setWidth(w);
  // }

  const handleSetHeight = (h) => {
    setHeight(h);
  }

  const [gallery, setGallery] = useState([])
  return (
    <>
      <Editor handleSetColor={handleSetColor} handleSetWidth={handleSetWidth} handleSetHeight={handleSetHeight}></Editor>
      <Output outputColor={color} outputWidth={width} outputHeight={height}></Output>
      <Gallery></Gallery>
    </>
  );
}

export default App;
