import React, {useState} from 'react';
import BorderRadius from './BorderRadius';
import '../scss/Editor.scss';

export default function Editor({colorRed}) {
    const [color, setColor] = useState("");
    const [width, setWidth] = useState("");
    const [height, setHeight] = useState("");


    return (
        <div>
            <div className='editor__inputs'>
                <div>
                    <h1>Choose color</h1>
                    <input type="color" id="color" onChange={(e) => setColor(e.target.value)}></input>
                    <h2>{color}</h2>
                </div>
                <div>
                    <h1>Choose size</h1>
                    <input type="range" id="width" name="width" min="0" max="500" value="100" step="10" onChange={(e) => setWidth(e.target.value)}></input>
                    <label for="width">Width</label>
                    <input type="range" id="height" name="height" min="0" max="500" value="100" step="10" onChange={(e) => setHeight(e.target.value)}></input>
                    <label for="height">Height</label>
                </div>
                <BorderRadius></BorderRadius>
            </div>
            <button>SAVE</button>
        </div>
    )
}
