import React, { useState, useEffect } from "react";
import Editor from "./components/Editor";
import Output from "./components/Output";
import Gallery from "./components/Gallery";

function App() {
  const [color, setColor] = useState("black");
  const [width, setWidth] = useState("50");
  const [height, setHeight] = useState("50");
  const [borderRadius, setBorderRadius] = useState("0");

  const handleSetColor = (c) => {
    setColor(c);
  };

  const handleSetWidth = (w) => {
    setWidth(w);
  };

  const handleSetHeight = (h) => {
    setHeight(h);
  };

  const handleSetBorderRadius = (b) => {
    setBorderRadius(b);
  };

  let rectsFromLocalStorage = localStorage.getItem("rects");

  if (!rectsFromLocalStorage) {
    rectsFromLocalStorage = [];
  }

  const [rects, setRects] = useState(rectsFromLocalStorage);

  const handleSaveRect = () => {
    console.log("handleSaveRect");
    const rect = {
      backgroundColor: color,
      width: width,
      height: height,
      borderRadius: borderRadius,
    };
    setRects((prevState) => {
      return [...prevState, rect];
    });
  };

  const handleRemoveRect = (r) => {
    setRects((prevState) => {
      prevState.splice(parseInt(r), 1);
      return [...prevState];
    });
  };

  console.log(rects);

  return (
    <>
      <Editor
        handleSetColor={handleSetColor}
        handleSetWidth={handleSetWidth}
        handleSetHeight={handleSetHeight}
        handleSetBorderRadius={handleSetBorderRadius}
        handleSaveRect={handleSaveRect}
      ></Editor>
      <Output
        outputColor={color}
        outputWidth={width}
        outputHeight={height}
        outputBorderRadius={borderRadius}
      ></Output>
      <Gallery
        rects={rects}
        handleRemoveRect={() => handleRemoveRect}
      ></Gallery>
    </>
  );
}

export default App;
