import React, {useState, useEffect} from 'react';
import Editor from './components/Editor';
import Output from './components/Output';
import Gallery from './components/Gallery';

function App() {

  const [color, setColor] = useState("");
  const [width, setWidth] = useState("");
  const [height, setHeight] = useState("");
  const [borderRadius, setBorderRadius] = useState("");

  const handleSetColor = (c) => {
    setColor(c);
  }

  const handleSetWidth = (w) => {
    setWidth(w);
  }

  const handleSetHeight = (h) => {
    setHeight(h);
  }

  const handleSetBorderRadius = (b) => {
    setBorderRadius(b);
  }

  const [rects, setRects] = useState([]);

  const handleSaveRect = () => {
    const rect = <div class='output-rect' style={{backgroundColor: color,
      width: `${width}px`,
      height: `${height}px`,
      borderRadius: `${borderRadius}px`}}></div>
    setRects(prevState => {
      return ([...prevState, rect])
    })
  }

  const handleRemoveRect = (r) => {
    setRects(prevState => {
      const prev = [...prevState];
      console.log(prev);
      console.log(r);
      return ([prev.slice(r, 1)])
    })
  }

  return (
    <>
      <Editor handleSetColor={handleSetColor} handleSetWidth={handleSetWidth} handleSetHeight={handleSetHeight} handleSetBorderRadius={handleSetBorderRadius} handleSaveRect={handleSaveRect}></Editor>
      <Output outputColor={color} outputWidth={width} outputHeight={height} outputBorderRadius={borderRadius}></Output>
      {rects && <Gallery rects={rects} handleRemoveRect={handleRemoveRect}></Gallery>}
    </>
  );
}

export default App;
