import React, {useState} from 'react';
import BorderRadius from './BorderRadius';
import '../scss/Editor.scss';

export default function Editor({handleSetColor, handleSetWidth, handleSetHeight}) {
    // const [color, setColor] = useState("");
    // const [width, setWidth] = useState("100");
    // const [height, setHeight] = useState("100");


    return (
        <div>
            <div className='editor__inputs'>
                <div>
                    <h1>Choose color</h1>
                    <input type="color" id="color" onChange={(e) => handleSetColor(e.target.value)}></input>
                    <h2></h2>
                </div>
                <div>
                    <h1>Choose size</h1>
                    <div>
                        <input type="range" id="width" name="width" min="0" max="500" value="0" step="10" onChange={(e) => handleSetWidth(e.target.value)}></input>
                        <label for="width">Width</label>
                        <p></p>
                    </div>
                    <div>
                        <input type="range" id="height" name="height" min="0" max="500" value="100" step="10" onChange={(e) => handleSetHeight(e.target.value)}></input>
                        <label for="height">Height</label>
                        <p></p>
                    </div>
                </div>
                <BorderRadius></BorderRadius>
            </div>
            <button>SAVE</button>
        </div>
    )
}
