import React, {useState, useEffect} from 'react';
import Editor from './components/Editor';
import Output from './components/Output';
import Gallery from './components/Gallery';

function App() {

  const [color, setColor] = useState("black");
  const [width, setWidth] = useState("50");
  const [height, setHeight] = useState("50");
  const [borderRadius, setBorderRadius] = useState("0");

  const handleSetColor = (c) => {
    setColor(c);
  }

  const handleSetWidth = (w) => {
    setWidth(w);
  }

  const handleSetHeight = (h) => {
    setHeight(h);
  }

  const handleSetBorderRadius = (b) => {
    setBorderRadius(b);
  }

  const [rects, setRects] = useState([]);

  // const handleSaveRect = () => {
  //   const rect = <div class='output-rect' style={{backgroundColor: color,
  //     width: `${width}px`,
  //     height: `${height}px`,
  //     borderRadius: `${borderRadius}px`}}></div>
  //   setRects(prevState => {
  //     return ([...prevState, rect])
  //   });
  //   console.log(rects)
  // }



  const handleSaveRect = () => {
    console.log('handleSaveRect');
    const rect = {
      backgroundColor: color,
      width: width,
      height: height,
      borderRadius: borderRadius
    };
    setRects(prevState => {
      return ([...prevState, rect])
    });
  }

  const handleRemoveRect = (r) => {
    setRects(prevState => {
      prevState.splice(parseInt(r), 1);
      return [...prevState]
    })
  }

  useEffect(() => {
    localStorage.setItem('rects', JSON.stringify(rects))
  }, [rects.length])

  return (
    <>
      <Editor handleSetColor={handleSetColor} handleSetWidth={handleSetWidth} handleSetHeight={handleSetHeight} handleSetBorderRadius={handleSetBorderRadius} handleSaveRect={handleSaveRect}></Editor>
      <Output outputColor={color} outputWidth={width} outputHeight={height} outputBorderRadius={borderRadius}></Output>
      {rects.length && <Gallery rects={localStorage.getItem('rects')} handleRemoveRect={() => handleRemoveRect}></Gallery>}
    </>
  );
}

export default App;
